import {Dispatch} from "redux";
import {USER_INFO_FAIL, USER_INFO_LOADING, USER_INFO_SUCCESS, userDispatchTypes} from "./UserActionType";
import axios from "axios";

export const GetUserInfo = (userCount: number) => async (dispatch: Dispatch<userDispatchTypes>) => {
  try {
    dispatch({
      type: USER_INFO_LOADING
    })
    const res = await axios.get(`https://randomuser.me/api/?results=${userCount}`);

    dispatch({
      type: USER_INFO_SUCCESS,
      payload: res.data
    })

  } catch(e) {
    dispatch({
      type: USER_INFO_FAIL
    })
  }
};
