import { Dispatch } from "redux"
import { ActionType } from "../user-action-type"
import { Action } from "../user-action"
import UserService from "../../UserService"

interface IPost {
    results: [
      {
        gender: string;
        name: {
          title: string;
          first: string;
          last: string;
        };
        location: {
          city: string;
        };
        dob: {
          date: string;
        };
      }
    ];
  }

export function depositMoney  (amount: number) {
    return async (dispatch: Dispatch<Action>) => {
    const response = await UserService.getUserInfo(); 
        return response
    }
}

